import { BrowserRouter, Routes, Route } from "react-router-dom";
import { Home , Question, Result} from "./pages/index";
import { Header,Footer } from "./components/index";
import { useState } from "react";
import { axios } from "axios";

const Router = () => {
    const[name,setName] = useState("");
    const[questions,setQuestions] = useState("");
    const[score,setScore] = useState(0); 

    const getQuestions = async() => {
        const { data } =await axios.get(`https://opentdb.com/api.php?amount=10&category=9&difficulty=easy&type=multiple`);
      setQuestions(data.results)       
      }
    return(
        <>
        <BrowserRouter>
        <Header />
        <Routes>
            <Route path="/" exact element={<Home name={name} setName={setName} getQuestions={getQuestions}/>}/>
            <Route path="/question" exact element={<Question 
                questions={questions}
                setQuestions={setQuestions}
                score={score}
                setScore={setScore}
            />}/>
            <Route path="/result" exact element={<Result />}/>
        </Routes>
        <Footer />
        </BrowserRouter>

        </>
    )
}

export default Router;
