import { Button } from "@mui/material";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { MainDiv, Options, SingleQuestion,OptionsBtn,Select,Wrong} from "./style";

const Question = ({
    currQuestion,
    setCurrQuestion,
    questions,
    options,
    correct,
    score,
    setScore,
}) => {
    const navigate = useNavigate();
    const[selected,setSelected] = useState()
    const[err,setErr] = useState(false)

    const handleSelect = (i) => {
        if(selected === i && selected === correct){
            return <Select />;
        }else if(selected === i && selected !== correct){
            return <Wrong />;
        }else if(i === correct){
            return <Select />;
        }
    }

    const handleCheck = (i) => {
        setSelected(i)
        if(i === correct){
            setScore(score + 1)
        }
        setScore(false)
    }

    const handleQuit = () => {

    }

    const handleNext = () => {
        if(currQuestion > 8){
            navigate('/result')
        }else if(selected){
            setCurrQuestion(currQuestion + 1);
            setSelected();
        }else{
            setErr("Please select an option first!")
        }
    }

    return(
        <>
        <MainDiv>
            <h1>Question {currQuestion + 1}</h1>

            <SingleQuestion>
                <h2>{questions[currQuestion].question}</h2>

                <Options className="option">
                        {err && <p>Choose appropriate ans!</p>}
                        {options && options.map((i) => <OptionsBtn
                        onClick={() => handleCheck(i)}
                        className={`${selected && handleSelect(i)}`}
                        >{i}</OptionsBtn>)}
                </Options>

                <div>
                     <Button
                     variant="contained"
                     color="secondary"
                     size="large"
                     style={{width : 185}}
                     href="/"
                     onClick={handleQuit}
                     >
                         Quit
                     </Button>
                     <Button
                         variant="contained"
                         color="primary"
                         size="large"
                         style={{width : 185}}
                         onClick={handleNext}
                     >
                         Next Question
                     </Button>
                </div>  
            </SingleQuestion>

        </MainDiv>
        </>
    )
}

export default Question;
