import { useState } from "react";
import { MainDiv, Options, SingleQuestion,OptionsBtn} from "./style";

const Question = ({
    currQuestion,
    setCurrQuestion,
    questions,
    options,
    correct,
    score,
    setScore,
    setQuestions
}) => {
    const[selected,setSelected] = useState()
    const[err,setErr] = useState(false)

    const handleSelect = (i) => {
        if(selected === i && selected === correct){
            return 'selected';
        }else if(selected === i && selected !== correct){
            return 'wrong';
        }else if(i === correct){
            return 'select';
        }
    }

    return(
        <>
        <MainDiv>
            <h1>Question {currQuestion + 1}</h1>

            <SingleQuestion>
                <h2>{questions[currQuestion].question}</h2>

                <Options className="option">
                        {err && <p>Choose appropriate ans!</p>}
                        {options && options.map((i) => <OptionsBtn
                        className={`${selected && handleSelect(i)}`}
                        >{i}</OptionsBtn>)}
                </Options>

                <div>
                        <button>Cancel</button>
                        <button>Next Question</button>
                </div>  
            </SingleQuestion>

        </MainDiv>
        </>
    )
}

export default Question;
