import { BrowserRouter, Routes, Route } from "react-router-dom";
import { Home , Question, Result} from "./pages/index";
import { Header,Footer } from "./components/index";
import { useState } from "react";

const Router = () => {
    const[name,setName] = useState("");
    const[questions,setQuestions] = useState("");
    // const[score,setScore] = useState(0); 

    const getQuestions = async(category = '',difficulty = '') => {
      await fetch(`https://opentdb.com/api.php?amount=10&category=${category}&difficulty=${difficulty}&type=multiple`)
        // .then(resp =>{
        //     console.log(resp.results)
        // })
        // .catch(error => {
        //     console.log(error)
        // })
       .then((res) => res.json())
       .then((json) => {
           setQuestions(json.results)
       })
      }
    return(
        <>
          <Header />
        <BrowserRouter>
        <Routes>
            <Route path="/" exact element={<Home name={name} setName={setName} getQuestions={getQuestions}/>}/>
            <Route path="/question" exact element={<Question 
                name={name}
                questions={questions}
                setQuestions={setQuestions} 
                // score={score}
                // setScore={setScore}
            />}/>
            <Route path="/result" exact element={<Result />}/>
        </Routes>
        </BrowserRouter>
        <Footer />

        </>
    )
}

export default Router;
